import React from 'react';
import styled from '@emotion/styled';
import Card from '@mui/material/Card';
import Typography from '@mui/material/Typography';

const StyledCard = styled(Card)`
  border: 2px solid black;
  border-radius: 8px;
  box-shadow: none;
  color: #0A0909;
  padding: 16px;
  margin-bottom: 16px;
`;

const ThemeID = styled('div')`
  display: flex;
  gap: 16px;
`;

const Label = styled(Typography)`
  font-weight: 700;
`;

const NumberID = styled(Typography)`
  color: #395FC2;
  font-weight: 700;
`;

const Text = styled(Typography)`
  margin-top: 8px;
  color: #AEAEAE;
`;

const ProbabilityDisplay = styled.div`
  display: flex;
  flex-direction: column;
  gap: 8px;
  opacity: 75%;
  margin-top: 16px;
  font-weight: 700;
`;

const ThemeProbability = styled.span`
  &.very-unlikely {
    color: #f77e6e;
  }
  &.unlikely {
    color: #f0c743;
  }
  &.likely {
    color: #1cd47e;
  }
  &.very-likely {
    color: #17f117;
  } 
`;

function renderProbability(value) {
  const assignRules = [
    [[0, 30], 'very-unlikely'],
    [[30, 55], 'unlikely'],
    [[55, 75], 'likely'],
    [[75, 100], 'very-likely'],
  ];

  const themeProbabilityClass = assignRules.find((v) => {
    const min = v[0][0];
    const max = v[0][1];
    return min <= value && max > value;
  });

  return <ThemeProbability className={themeProbabilityClass[1]}>
    {value}%
  </ThemeProbability>;
}

export function ThemeCard({ index, suggestions }) {
  return (
    <StyledCard>
      <ThemeID>
        <Label>Tema Repetitivo</Label>
        <NumberID>{index}</NumberID>
      </ThemeID>
      <ProbabilityDisplay>
        {suggestions.map((suggestion, idx) => (
          <div key={idx}>
            <Typography variant="body2">{`Sugestão ${idx + 1}: ${suggestion.suggestion}`}</Typography>
            <Typography variant="body2">Similaridade: {renderProbability(suggestion.similarity)}</Typography>
          </div>
        ))}
      </ProbabilityDisplay>
    </StyledCard>
  );
}
