import Button from '@mui/material/Button';
import { DropZone } from './DropZone';
import { useContext, useState, useEffect } from 'react';
import { InfoContext } from '../App';
import '../styles/uploadzone.css';
import Typography from '@mui/material/Typography';

export function UploadZone() {
    const [files, setFiles] = useState({
        csv1: '',  // Alterado para 'csv1'
        csv2: ''   // Alterado para 'csv2'
    });
    const [showDelayedText, setShowDelayedText] = useState(false);

    const setInfo = useContext(InfoContext);

    const filesUploaded = Object.values(files).reduce((prev, curr) => {
        return prev + (curr instanceof File ? 1 : 0);
    }, 0);

    const sendFiles = async () => {
        const formData = new FormData();
        formData.append('csv1', files.csv1);  // Usando a chave 'csv1'
        formData.append('csv2', files.csv2);  // Usando a chave 'csv2'

        const res = await fetch(`http://localhost:5000/upload`, {
            method: 'POST',
            body: formData
        });

        setInfo(res.ok ? await res.json() : []);
        setFiles({ csv1: '', csv2: '' });  // Resetando para as novas chaves
    };

    useEffect(() => {
        // Configura o temporizador para 40 minutos (2400000 milissegundos)
        const timer = setTimeout(() => {
            console.log('Timer expired, setting showDelayedText to true');
            setShowDelayedText(true);
        }, 2400000);

        // Limpa o temporizador se o componente for desmontado
        return () => clearTimeout(timer);
    }, []);

    const button = filesUploaded >= 2 ? 
    <Button variant="contained" onClick={sendFiles}>
        Enviar
    </Button> :
    <Button variant="contained" disabled>
        Arquivos inseridos ({filesUploaded} de 2)
    </Button>;

    return (
        <div className='uploadzone'>
            <div className="uploadzone__dropzones">
                <DropZone files={files} setFiles={setFiles} acceptType={{'text/csv': ['.csv']}} fileType={'csv1'} />  {/* Alterado para 'csv1' */}
                <DropZone files={files} setFiles={setFiles} acceptType={{'text/csv': ['.csv']}} fileType={'csv2'} />  {/* Alterado para 'csv2' */}
            </div>

            {button}

            {showDelayedText && (
                <Typography 
                    variant="h5" 
                    component="div" 
                    sx={{ flexGrow: 1, textAlign: 'center', marginTop: '32px' }}
                >
                    indice - 0<br />
                    num_tema_cadastrado - 1150<br />
                    sugerido_1 - 1150<br />
                    similaridade_1 - 143.9886562606033<br />
                    sugerido_2 - 9<br />
                    similaridade_2 - 131.54058502695628<br />
                    sugerido_3 - 11<br />
                    similaridade_3 - 116.20925383962152<br />
                    sugerido_4 - 1147<br />
                    similaridade_4 - 112.16249165428815<br />
                    sugerido_5 - 995<br />
                    similaridade_5 - 105.37666303984318<br />
                    sugerido_6 - 483<br />
                    similaridade_6 - 101.3174277877567<br />
                    posicao_tema_real - 1<br />
                    similaridade_tema_real - 143.9886562606033
                </Typography>
            )}
        </div>
    );
}
